package com.lovomall.csc.entity;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.hibernate.annotations.GenericGenerator;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.JoinColumn;import javax.persistence.ManyToOne;import javax.persistence.Table;import java.sql.Date;/** * Author:     cafebabe * Date:       2019/10/10  * Time:       下午4:35 * Description: 消费记录实体 * Version: 1.0 */@Setter@Getter@NoArgsConstructor@AllArgsConstructor@Entity@Table (name = "t_consume")public class ConsumeRecord {    /** 消费记录id*/    @Id    @GenericGenerator (name = "conUUID", strategy = "uuid")    @GeneratedValue (generator = "conUUID")    @Column (name = "consu_id", length = 32)    private String consuId;    /** 用户余额对象*/    @ManyToOne    @JoinColumn(name = "baId",nullable = false)    private Balance balance;    /** 订单id*/    @Column(name = "order_Id", length = 32, nullable = false)    private String orderId;    /** 下单日期*/    @Column (name = "order_date", length = 20, nullable = false)    private Date orderDate;    /** 审核状态*/    @Column (name = "order_status", length = 10, nullable = false)    private String orderStatus = "未审核";    /** 订单总价*/    @Column (name = "total_price", length = 10, nullable = false)    private double totalPrice;    /** 订单实际价格*/    @Column (name = "pay_money", length = 10, nullable = false)    private double payMoney = 0;}